package io.netty.test.test02;import io.netty.buffer.ByteBuf;import io.netty.channel.ChannelHandlerContext;import io.netty.handler.codec.ReplayingDecoder;import java.util.List;// 继承自 ReplayingDecoder 用于将字节解码为消息public class ToIntegerDecoder2 extends ReplayingDecoder {    /*******     * 将字节码转为消息     */    @Override    protected void decode(ChannelHandlerContext ctx, ByteBuf in, List<Object> out) throws Exception {        // 从入站 ByteBuf 读取整型，并添加到解码消息的 List 中        out.add(in.readInt());    }}