package io.netty.test.test03;import io.netty.buffer.ByteBuf;import io.netty.channel.ChannelHandlerContext;import io.netty.handler.codec.MessageToByteEncoder;public class MessageToUserEncoder extends MessageToByteEncoder<User> {    @Override    protected void encode(ChannelHandlerContext ctx, User msg, ByteBuf out) throws Exception {        out.writeInt(msg.getAge());        out.writeBytes(msg.getName().getBytes());    }}