package io.netty.test.test05;import io.netty.channel.Channel;import io.netty.channel.ChannelInitializer;import io.netty.channel.ChannelPipeline;import io.netty.handler.codec.http.HttpClientCodec;import io.netty.handler.codec.http.HttpServerCodec;import io.netty.handler.ssl.SslContext;import io.netty.handler.ssl.SslHandler;import javax.net.ssl.SSLEngine;public class HttpsCodecInitializer extends ChannelInitializer<Channel> {    private SslContext sslContext;    private boolean client;    public HttpsCodecInitializer(SslContext sslContext, boolean client) {        this.sslContext = sslContext;        this.client = client;    }    @Override    protected void initChannel(Channel ch) throws Exception {        ChannelPipeline pipeline = ch.pipeline();        SSLEngine engine = sslContext.newEngine(ch.alloc());        pipeline.addFirst("SSL", new SslHandler(engine));        if (client) {            pipeline.addLast("codec", new HttpClientCodec());        } else {            pipeline.addLast("codec", new HttpServerCodec());        }    }}